import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import { Row, Col, Image, ListGroup, Button, Card, Container } from "react-bootstrap";
import axios from "axios";
import Rating from "../Rating";

const ProductScreen = ({params}) => {
  const { id } = useParams(); // Get product ID from URL
  const [product, setProduct] = useState({});

  
  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const { data } = await axios.get(`${process.env.REACT_APP_API_URL}/api/products/${id}/`);
        setProduct(data);
        setLoading(false);
      } catch (err) {
        setError("Error fetching product details");
        setLoading(false);
      }
    };

    fetchProduct();
  }, [id]);

  return (
    <Container>
      <Row>
        <Col md={6}>
          <Image src={product.image} alt={product.productname} fluid />
        </Col>

        <Col md={3}>
          <ListGroup variant="flush">
            <ListGroup.Item>
              <h3>{product.productname}</h3>
            </ListGroup.Item>
            <ListGroup.Item>
              <Rating value={product.rating} text={`${product.numReviews} reviews`} color={"#f8e825"} />
            </ListGroup.Item>
            <ListGroup.Item>
              <strong>Brand:</strong> {product.productbrand}
            </ListGroup.Item>
            <ListGroup.Item>
              <strong>Description:</strong> {product.productinfo}
            </ListGroup.Item>
          </ListGroup>
        </Col>

        <Col md={3}>
          <Card>
            <ListGroup variant="flush">
              <ListGroup.Item>
                <Row>
                  <Col>Price:</Col>
                  <Col>
                    <strong>{product.price} Rs</strong>
                  </Col>
                </Row>
              </ListGroup.Item>
              <ListGroup.Item>
                <Row>
                  <Col>Status:</Col>
                  <Col>{product.countInStock > 0 ? "In Stock" : "Out of Stock"}</Col>
                </Row>
              </ListGroup.Item>
              <ListGroup.Item>
                <Button className="btn-block btn-success" disabled={product.countInStock === 0} type="button">
                  Add to Cart
                </Button>
              </ListGroup.Item>
            </ListGroup>
          </Card>
        </Col>
      </Row>

      {/* "Go Back" Button at the Bottom */}
      <div className="text-center mt-4">
        <Link to="/" className="btn btn-dark">
          Go Back
        </Link>
      </div>
    </Container>
  );
};

export default ProductScreen;
